import './App.css';
import { Outlet } from "react-router-dom";
import 'animate.css';

import React, { useState } from 'react';

const translations = {
  en: {
    welcome_message: "Hello, welcome to the React multi-language example!"
  },
  tr: {
    welcome_message: "Merhaba, React çoklu dil örneğine hoş geldiniz!"
  }
};

function App() {
  const [selectedLanguage, setSelectedLanguage] = useState('en');

  const changeLanguage = (language) => {
    setSelectedLanguage(language);
  };

  return (
    <div className='App'>
      <button onClick={() => changeLanguage('en')}>English</button>
      <button onClick={() => changeLanguage('tr')}>Türkçe</button>

      <p>{translations[selectedLanguage].welcome_message}</p>
      <Outlet />
    </div>
  );
}

export default App;
