import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Link } from "react-router-dom";
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import '../App.css';
import '../Styles/Clock.css';

function CountDown() {
  const [hour, setHour] = useState("00");
  const [minute, setMinute] = useState("00");
  const [second, setSecond] = useState("00");
  const [isActive, setIsActive] = useState(false);
  const [isCounting, setIsCounting] = useState(false);

  useEffect(() => {
    let timer;

    if (isActive && (parseInt(hour) > 0 || parseInt(minute) > 0 || parseInt(second) > 0)) {
      timer = setInterval(() => {
        let newHour = parseInt(hour);
        let newMinute = parseInt(minute);
        let newSecond = parseInt(second);

        if (newSecond > 0) {
          newSecond--;
        } else {
          if (newMinute > 0) {
            newMinute--;
            newSecond = 59;
          } else {
            if (newHour > 0) {
              newHour--;
              newMinute = 59;
              newSecond = 59;
            }
          }
        }

        setHour(String(newHour).padStart(2, "0"));
        setMinute(String(newMinute).padStart(2, "0"));
        setSecond(String(newSecond).padStart(2, "0"));

        if (newHour === 0 && newMinute === 0 && newSecond === 0) {
          setIsActive(false);
          setIsCounting(false);

          // Geri sayım tamamlandığında alert göster
          alert("Geri sayım tamamlandı!");
        }
      }, 1000);
    } else {
      clearInterval(timer);
    }

    return () => clearInterval(timer);
  }, [hour, minute, second, isActive]);

  const incrementHour = () => setHour(String(parseInt(hour) + 1).padStart(2, "0"));
  const decrementHour = () => setHour(String(Math.max(0, parseInt(hour) - 1)).padStart(2, "0"));

  const incrementMinute = () => setMinute(String(parseInt(minute) + 1).padStart(2, "0"));
  const decrementMinute = () => setMinute(String(Math.max(0, parseInt(minute) - 1)).padStart(2, "0"));

  const incrementSecond = () => setSecond(String(parseInt(second) + 1).padStart(2, "0"));
  const decrementSecond = () => setSecond(String(Math.max(0, parseInt(second) - 1)).padStart(2, "0"));

  const startCountdown = () => {
    if (!isCounting) {
      if (hour === "00" && minute === "00" && second === "00") {

        toast.warn("Değerleri ayarlayın!", {
          position: "bottom-center",
          autoClose: 3000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: "dark",
        });
      } else {
        setIsActive(true);
        setIsCounting(true);
      }
    }
  };

  const stopCountdown = () => {
    if (isActive) {
      setIsActive(false);
      setIsCounting(false);

      if (hour !== "00" || minute !== "00" || second !== "00") {
        toast.info("Geri sayım durdu!", {
          position: "bottom-center",
          autoClose: 3000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: "dark",
        });
      }
    }
  };

  const resetCountdown = () => {
    setIsActive(false);
    setIsCounting(false);
    setHour("00");
    setMinute("00");
    setSecond("00");

    if (hour !== "00" || minute !== "00" || second !== "00") {
      toast.success("Değerler sıfırlandı!", {
        position: "bottom-center",
        autoClose: 3000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
    }
  };

  return (
    <div>
      <div className="clock">
        <div className="flex flex-col items-center">
          <button type="button" style={{ fontSize: 65, color: '#b1b2ff' }} onClick={incrementHour}>+</button>
          <div className="hour">{hour}</div>
          <button type="button" style={{ fontSize: 65, color: '#b1b2ff' }} onClick={decrementHour}>-</button>
        </div>

        <div className="colon flex flex-col justify-center">:</div>

        <div className="flex flex-col items-center">
          <button type="button" style={{ fontSize: 65, color: '#ffb3b3' }} onClick={incrementMinute}>+</button>
          <div className="minute">{minute}</div>
          <button type="button" style={{ fontSize: 65, color: '#ffb3b3' }} onClick={decrementMinute}>-</button>
        </div>

        <div className="colon flex flex-col justify-center">:</div>

        <div className="flex flex-col items-center">
          <button type="button" style={{ fontSize: 65, color: '#ECA869' }} onClick={incrementSecond}>+</button>
          <div className="second">{second}</div>
          <button type="button" style={{ fontSize: 65, color: '#ECA869' }} onClick={decrementSecond}>-</button>
        </div>
      </div>

      <div className="flex flex-row justify-between" style={{ paddingLeft: 60, paddingRight: 60 }}>
        <div>
          <Link to="/" className="button" style={{ backgroundColor: "#e27f23" }}>Saat'e Dön</Link>
        </div>

        <div className="flex flex-row justify-center">
          <button className="button" style={{ backgroundColor: '#b1b2ff' }} onClick={startCountdown}>
            Başlat
          </button>
          <button className="button ml-4" style={{ backgroundColor: '#ffb3b3' }} onClick={stopCountdown}>
            Durdur
          </button>
          <button className="button ml-4" style={{ backgroundColor: '#ECA869' }} onClick={resetCountdown}>
            Sıfırla
          </button>
        </div>
      </div>

      <ToastContainer
        position="bottom-center"
        autoClose={5000}
        limit={3}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable={false}
        pauseOnHover
        theme="dark"
      />
    </div>
  );
}

export default CountDown;
