import './App.css';
import { Outlet } from "react-router-dom";
import 'animate.css';

import React, { useState, useEffect } from 'react';

function App() {
  const [selectedLanguage, setSelectedLanguage] = useState(localStorage.getItem('preferredLanguage') || 'tr-TR');
  const [translations, setTranslations] = useState({});
  const [loading, setLoading] = useState(true); // Yükleme durumu

  const changeLanguage = (language) => {
    setSelectedLanguage(language);
  };

  useEffect(() => {
    const storedLanguage = localStorage.getItem('preferredLanguage');
    if (storedLanguage) {
      setSelectedLanguage(storedLanguage);
    }
  }, []);

  useEffect(() => {
    import(`./Lang/${selectedLanguage}.json`)
      .then((module) => {
        setTranslations(module.default);
        setLoading(false); // Yükleme tamamlandı
      })
      .catch((error) => console.error(error));
  }, [selectedLanguage]);

  return (
    <div>
      <div>
        <button onClick={() => changeLanguage('en-EN')}>English</button>
        <button onClick={() => changeLanguage('tr-TR')}>Türkçe</button>
      </div>

      {loading ? (
        <p>Loading...</p> // Yükleme durumu göster
      ) : (
        <p>{translations.welcome_message}</p>
      )}

<Outlet />
    </div>
  );
}

export default App;
