import './App.css';
import { Outlet } from "react-router-dom";
import 'animate.css';
import React, { useState, useEffect } from 'react';

const translations = {
  en: {
    welcome_message: "Hello, welcome to the React multi-language example!"
  },
  tr: {
    welcome_message: "Merhaba, React çoklu dil örneğine hoş geldiniz!"
  }
};

function App() {
  const [selectedLanguage, setSelectedLanguage] = useState(localStorage.getItem('preferredLanguage') || 'en');

  const changeLanguage = (language) => {
    setSelectedLanguage(language);
  };

  useEffect(() => {
    // Dil tercihini localStorage'dan al
    const storedLanguage = localStorage.getItem('preferredLanguage');
    if (storedLanguage) {
      setSelectedLanguage(storedLanguage);
    }
  }, []); // Boş bağımlılık dizisi, sadece ilk render'da çalışır

  useEffect(() => {
    // Dil tercihini localStorage'e kaydet
    localStorage.setItem('preferredLanguage', selectedLanguage);
  }, [selectedLanguage]);

  return (
    <div>
      <div>
        <button onClick={() => changeLanguage('en')}>English</button>
        <button onClick={() => changeLanguage('tr')}>Türkçe</button>
      </div>

      <p>{translations[selectedLanguage].welcome_message}</p>
      <Outlet />
    </div>
  );
}

export default App;
