import React, { useState, useEffect } from 'react';
import '../Styles/Clock.css';
import { BrowserRouter as Router, Link } from "react-router-dom";



function Pomodoro() {
  const [running, setRunning] = useState(false);
  const [workMode, setWorkMode] = useState(true); // true for work, false for break
  const [minutes, setMinutes] = useState(1);
  const [seconds, setSeconds] = useState(0);
  const [showModal, setShowModal] = useState(false);

  const closeModal = () => {
    setIsModalOpen(false);
  };

  useEffect(() => {
    let timer;

    if (running) {
      timer = setInterval(() => {
        if (minutes === 0 && seconds === 0) {
          // Switch between work and break modes
          if (workMode) {
            setShowModal(true);
          } else {
            setWorkMode(true);
            setMinutes(25); // 25-minute work session
            setSeconds(0);
          }
        } else if (seconds === 0) {
          setMinutes(minutes - 1);
          setSeconds(59);
        } else {
          setSeconds(seconds - 1);
        }
      }, 1000);
    } else {
      clearInterval(timer);
    }

    return () => clearInterval(timer); // Cleanup timer on unmount
  }, [running, minutes, seconds, workMode]);

  const startTimer = () => {
    setRunning(true);
  };

  const stopTimer = () => {
    setRunning(false);
  };

  const handleModalConfirm = () => {
    setShowModal(false);
    setWorkMode(false);
    setMinutes(5); // 5-minute break
    setSeconds(0);
  };

  const handleModalCancel = () => {
    setShowModal(false);
  };

  return (
    <div>
      <div className="clock">
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center"
          }}
        >
          <div className="hour">{String(minutes).padStart(2, '0')}</div>
          <button className="button" style={{ backgroundColor: '#b1b2ff' }} onClick={startTimer}>
            Başlat
          </button>
        </div>
        <div className="colon">:</div>

        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center"
          }}
        >
          <div className="minute">{String(seconds).padStart(2, '0')}</div>
          <button className="button" style={{ backgroundColor: '#ffb3b3' }} onClick={stopTimer}>
            Durdur
          </button>
        </div>
      </div>
      <p>{workMode ? "Çalışma Modu" : "Mola Modu"}</p>

    </div>
  );
}

export default Pomodoro;
