import React, { useState, useEffect } from 'react';

function App() {
  const [selectedLanguage, setSelectedLanguage] = useState(localStorage.getItem('preferredLanguage') || 'tr-TR');
  const [translations, setTranslations] = useState({});
  const [loading, setLoading] = useState(true);

  const changeLanguage = (language) => {
    setSelectedLanguage(language);
  };

  useEffect(() => {
    const storedLanguage = localStorage.getItem('preferredLanguage');
    if (storedLanguage) {
      setSelectedLanguage(storedLanguage);
    } else {
      localStorage.setItem('preferredLanguage', 'tr-TR'); // Sayfa ilk yüklendiğinde "tr-TR" olarak ayarla
    }
  }, []);

  useEffect(() => {
    import(`./Lang/${selectedLanguage}.json`)
      .then((module) => {
        setTranslations(module.default);
        setLoading(false);
      })
      .catch((error) => console.error(error));
  }, [selectedLanguage]);

  return (
    <div>
      <div>
        <button style={{ color: '#fff' }} onClick={() => changeLanguage('en-EN')}>English</button>
        <button style={{ color: '#fff' }} onClick={() => changeLanguage('tr-TR')}>Türkçe</button>
      </div>

      {loading ? (
        <p style={{ color: '#fff' }}>Loading...</p>
      ) : (
        <p style={{ color: '#fff' }}>{translations.welcome_message}</p>
      )}

      {/* Burada sayfanın geri kalanını ekleyebilirsiniz */}
    </div>
  );
}

export default App;
