// LanguageManager.js

let selectedLanguage = 'tr'; // Varsayılan dil, isteğe bağlı olarak ayarlayabilirsiniz

const translations = {
  tr: require('./tr.json'),
  en: require('./en.json'),
  // Diğer dilleri ekleyin
};

export function setLanguage(lang) {
  if (translations[lang]) {
    selectedLanguage = lang;
    // Seçilen dili önbellekte saklamak için localStorage veya başka bir yöntem kullanabilirsiniz
  }
}

export function getTranslation(key) {
  return translations[selectedLanguage][key] || key;
}
