import { getSelectedLanguage, setSelectedLanguage } from './Lang/LanguageManagement';
const App = () => {
  return (
    <div>
      <h1 style={{color:'red'}}>{translate('closeButton', getSelectedLanguage())}</h1>

      <LanguageSelector />
      <Outlet />
    </div>
  );
};

export default App;
