import './App.css';
import { Outlet } from "react-router-dom";
import 'animate.css';

import React, { useState, useContext } from 'react';

import tr from './Lang/tr-TR.json';
import en from './Lang/en-EN.json';

function loadLanguage(locale) {
  switch (locale) {
    case 'tr':
      return tr;
    case 'en':
      return en;
    default:
      return en; // Varsayılan dil
  }
}

// Dil Context'i
const LanguageContext = React.createContext();

// Dil seçimini yapan bileşen
function LanguageSelector() {
  const { setLocale } = useContext(LanguageContext);

  const changeLanguage = (locale) => {
    // Kullanıcının seçtiği dili ayarla
    setLocale(locale);
  };

  return (
    <div>
      <button style={{color:'#fff'}} onClick={() => changeLanguage('tr')}>Türkçe</button>
      <button style={{color:'#fff'}} onClick={() => changeLanguage('en')}>English</button>
    </div>
  );
}

// Uygulama bileşeni
function App() {
  const [locale, setLocale] = useState('en'); // Varsayılan dil: İngilizce
  const languageData = loadLanguage(locale);

  return (
    <div>
      <LanguageContext.Provider value={{ setLocale }}>
        <LanguageSelector />
        <h1 style={{color:'#fff'}} >{languageData.welcome_message}</h1>
      </LanguageContext.Provider>

      <Outlet />
    </div>
  );
}

export default App;
