// LanguageManager.js

let selectedLanguage = 'en'; // Varsayılan dil, isteğe bağlı olarak ayarlayabilirsiniz

const translations = {
  tr: {
    welcome: 'Hoş geldiniz',
    hello: 'Merhaba'
    // Diğer çeviriler
  },
  en: {
    welcome: 'Welcome',
    hello: 'Hello'
    // Diğer çeviriler
  }
  // Diğer dilleri ekleyin
};

export function setLanguage(lang) {
  if (translations[lang]) {
    selectedLanguage = lang;
    localStorage.setItem('selectedLanguage', lang); // Dil bilgisini önbellekte sakla
  }
}

export function getTranslation(key) {
  return translations[selectedLanguage][key] || key;
}

// Sayfa yüklendiğinde önbellekteki dil bilgisini kontrol et
const cachedLanguage = localStorage.getItem('selectedLanguage');
if (cachedLanguage && translations[cachedLanguage]) {
  selectedLanguage = cachedLanguage;
}
