// LanguageManager.js

const languageData = {
    tr: require('./tr.json'),
    en: require('./en.json'),
    // Diğer dilleri ekleyin
  };

  let selectedLanguage = 'en'; // Başlangıçta varsayılan dil olarak İngilizce'yi kullanıyoruz

  export function setLanguage(lang) {
    if (languageData[lang]) {
      selectedLanguage = lang;
      // Seçilen dili önbellekte saklamak için localStorage veya başka bir yöntem kullanabilirsiniz
    }
  }

  export function getTranslation(key) {
    return languageData[selectedLanguage][key] || key;
  }
