import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Link } from "react-router-dom";

import '../App.css';
import '../Styles/Clock.css';

function CountDown() {
  const [hour, setHour] = useState(0);
  const [minute, setMinute] = useState(0);
  const [second, setSecond] = useState(0);
  const [isActive, setIsActive] = useState(false); // Geri sayım aktif mi?

  useEffect(() => {
    let timer;

    if (isActive && (hour > 0 || minute > 0 || second > 0)) {
      timer = setInterval(() => {
        if (second > 0) {
          setSecond(second - 1);
        } else {
          if (minute > 0) {
            setMinute(minute - 1);
            setSecond(59);
          } else {
            if (hour > 0) {
              setHour(hour - 1);
              setMinute(59);
              setSecond(59);
            } else {
              // Geri sayım tamamlandı, burada istediğiniz bir işlemi yapabilirsiniz
              setIsActive(false);
            }
          }
        }
      }, 1000);
    } else {
      clearInterval(timer);
    }

    return () => clearInterval(timer);
  }, [hour, minute, second, isActive]);

  const incrementHour = () => setHour(hour + 1);
  const decrementHour = () => setHour(Math.max(0, hour - 1));

  const incrementMinute = () => setMinute(minute + 1);
  const decrementMinute = () => setMinute(Math.max(0, minute - 1));

  const incrementSecond = () => setSecond(second + 1);
  const decrementSecond = () => setSecond(Math.max(0, second - 1));

  const startCountdown = () => setIsActive(true);
  const stopCountdown = () => setIsActive(false);
  const resetCountdown = () => {
    setIsActive(false);
    setHour(0);
    setMinute(0);
    setSecond(0);
  };

  return (
    <div>
      <div className="clock">
        <div className="flex flex-col items-center">
          <button type="button" style={{ fontSize: 65, color:'#b1b2ff' }} onClick={incrementHour}>+</button>
          <div className="hour">{hour}</div>
          <button type="button" style={{ fontSize: 65, color:'#b1b2ff' }} onClick={decrementHour}>-</button>
        </div>

        <div className="colon flex flex-col justify-center" style={{marginBottom:90}}>:</div>

        <div className="flex flex-col items-center">
          <button type="button" style={{ fontSize: 65, color:'#ffb3b3' }} onClick={incrementMinute}>+</button>
          <div className="minute">{minute}</div>
          <button type="button" style={{ fontSize: 65, color:'#ffb3b3' }} onClick={decrementMinute}>-</button>
        </div>

        <div className="colon flex flex-col justify-center" style={{marginBottom:90}}>:</div>

        <div className="flex flex-col items-center">
          <button type="button" style={{ fontSize: 65, color:'#ECA869' }} onClick={incrementSecond}>+</button>
          <div className="second">{second}</div>
          <button type="button" style={{ fontSize: 65, color:'#ECA869' }} onClick={decrementSecond}>-</button>
        </div>
      </div>

      <div className="flex justify-center">
        <button className="start-stop-button" onClick={startCountdown}>
          Başlat
        </button>
        <button className="start-stop-button" onClick={stopCountdown}>
          Durdur
        </button>
        <button className="start-stop-button" onClick={resetCountdown}>
          Sıfırla
        </button>
      </div>

      <Link to="/" className="button" style={{ backgroundColor: "#e27f23", marginTop:40 }}>Saat'e Dön</Link>
    </div>
  );
}

export default CountDown;
