// useLanguage.js

import { useState, useEffect } from 'react';

const translations = {
  tr: require('./tr.json'),
  en: require('./en.json'),
  // Diğer dilleri ekleyin
};

const useLanguage = () => {
  const [language, setLanguage] = useState('en'); // Varsayılan dil

  const changeLanguage = (newLanguage) => {
    if (translations[newLanguage]) {
      setLanguage(newLanguage);
      localStorage.setItem('selectedLanguage', newLanguage); // Dil bilgisini önbellekte sakla
    }
  };

  useEffect(() => {
    const cachedLanguage = localStorage.getItem('selectedLanguage');
    if (cachedLanguage && translations[cachedLanguage]) {
      setLanguage(cachedLanguage);
    }
  }, []);

  const translate = (key) => {
    return translations[language][key] || key;
  };

  return { language, changeLanguage, translate };
};

export default useLanguage;
