import './App.css';
import { Outlet } from "react-router-dom";
import 'animate.css';

import React, { useState, useEffect } from 'react';

function App() {
  const [selectedLanguage, setSelectedLanguage] = useState('tr-TR'); // Başlangıçta "tr-TR" dilini seçili olarak ayarla
  const [translations, setTranslations] = useState({});
  const [loading, setLoading] = useState(true);

  const changeLanguage = (language) => {
    setSelectedLanguage(language);
    localStorage.setItem('preferredLanguage', language); // Dil değiştikçe tercihi sakla
  };

  useEffect(() => {
    const storedLanguage = localStorage.getItem('preferredLanguage');
    if (storedLanguage) {
      setSelectedLanguage(storedLanguage);
    }
  }, []);

  useEffect(() => {
    import(`./Lang/${selectedLanguage}.json`)
      .then((module) => {
        setTranslations(module.default);
        setLoading(false);
      })
      .catch((error) => console.error(error));
  }, [selectedLanguage]);

  return (
    <div>
      <div>
        <button style={{color:'#fff'}} onClick={() => changeLanguage('en-EN')}>English</button>
        <button style={{color:'#fff'}} onClick={() => changeLanguage('tr-TR')}>Türkçe</button>
      </div>

      {loading ? (
        <p style={{color:'#fff'}}>Loading...</p>
      ) : (
        <Outlet />
      )}


    </div>
  );
}

export default App;
