import './App.css';
import { Outlet } from "react-router-dom";
import 'animate.css';
import { LanguageProvider } from './Lang/LanguageProvider'; // LanguageContext'ten useLanguage'ı içe aktarın

import React from 'react';

function App() {
  const { loading, translations, changeLanguage } = LanguageProvider(); // useLanguage özelliğini kullanarak translations ve loading'e erişin

  return (

    <LanguageProvider>
      <div>
        <button style={{ color: '#fff' }} onClick={() => changeLanguage('en-EN')}>English</button>
        <button style={{ color: '#fff' }} onClick={() => changeLanguage('tr-TR')}>Türkçe</button>
      </div>

      <p style={{ color: '#fff' }}>{translations.welcome_message}</p>


      <div>
        <Outlet />
      </div>
    </LanguageProvider>
  );
}

export default App;
