// LanguageSelector.js

import React from 'react';
import { useLanguage } from './LanguageContext';

function LanguageSelector() {
  const { selectedLanguage, changeLanguage } = useLanguage();

  return (
    <div>
      <button onClick={() => changeLanguage('tr')} disabled={selectedLanguage === 'tr'}>
        Türkçe
      </button>
      <button onClick={() => changeLanguage('en')} disabled={selectedLanguage === 'en'}>
        English
      </button>
    </div>
  );
}

export default LanguageSelector;
