import React, { createContext, useContext, useState } from 'react';

// Dil bağlamını oluştur
const LanguageContext = createContext();

// Dil bağlamı sağlayıcısı
export function LanguageProvider({ children }) {
  const [selectedLanguage, setSelectedLanguage] = useState('tr');

  return (
    <LanguageContext.Provider value={{ selectedLanguage, setSelectedLanguage }}>
      {children}
    </LanguageContext.Provider>
  );
}

// Dil bağlamı kullanma özel kancası
export function useLanguage() {
  return useContext(LanguageContext);
}

// Çeviri işlevi
function getTranslation(key, language) {
  const translations = {
    tr: {
      welcomeMessage: 'Merhaba, React Uygulaması',
    },
    en: {
      welcomeMessage: 'Hello, React App',
    },
  };

  return translations[language][key] || key;
}

// Dil seçimi bileşeni
function LanguageSelector() {
  const { selectedLanguage, setSelectedLanguage } = useLanguage();

  const handleLanguageChange = (newLanguage) => {
    setSelectedLanguage(newLanguage);
  };

  return (
    <div>
      <button onClick={() => handleLanguageChange('tr')}>Türkçe</button>
      <button onClick={() => handleLanguageChange('en')}>English</button>
    </div>
  );
}

// Karşılama mesajı bileşeni
function WelcomeMessage() {
  const { selectedLanguage } = useLanguage();

  return (
    <div>
      <h1>{getTranslation('welcomeMessage', selectedLanguage)}</h1>
    </div>
  );
}

// Ana uygulama bileşeni
function App() {
  return (
    <LanguageProvider>
      <div>
        <LanguageSelector />
        <WelcomeMessage />
      </div>
    </LanguageProvider>
  );
}

export default App;
