import React, { useState, useEffect } from 'react';
import '../Styles/Clock.css';
import { BrowserRouter as Router, Link } from "react-router-dom";

function Pomodoro() {
  const [running, setRunning] = useState(false);
  const [workMode, setWorkMode] = useState(true); // true for work, false for break
  const [minutes, setMinutes] = useState(25);
  const [seconds, setSeconds] = useState(0);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    let timer;

    if (running) {
      timer = setInterval(() => {
        if (minutes === 0 && seconds === 0) {
          // Switch between work and break modes
          if (workMode) {
            setIsModalOpen(true); // Open the modal when the work session is complete
          } else {
            setWorkMode(true);
            setMinutes(25); // 25-minute work session
            setSeconds(0);
            setRunning(false); // Stop the timer after work session is complete
          }
        } else if (seconds === 0) {
          setMinutes(minutes - 1);
          setSeconds(59);
        } else {
          setSeconds(seconds - 1);
        }
      }, 1000);
    } else {
      clearInterval(timer);
    }

    return () => clearInterval(timer); // Cleanup timer on unmount
  }, [running, minutes, seconds, workMode]);

  const startTimer = () => {
    setRunning(true);
  };

  const stopTimer = () => {
    setRunning(false);
  };

  const closeModal = () => {
    setIsModalOpen(false);

    // Start the break session when the user clicks "Tamam" in the modal
    if (workMode) {
      setWorkMode(false);
      setMinutes(5); // 5-minute break
      setSeconds(0);
      setRunning(true); // Start the timer for the break session
    }
  };

  return (
    <div>
      {isModalOpen && (
        <div id="popup-modal" className="modal">
          <div className="modal-content">
            <p>Countdown süreniz Tamamlandı!</p>
            <button className="modal-button" onClick={closeModal}>Tamam</button>
          </div>
        </div>
      )}

      <div className="clock">
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center"
          }}
        >
          <div className="hour">{String(minutes).padStart(2, '0')}</div>
          <button className="button" style={{ backgroundColor: '#b1b2ff' }} onClick={startTimer}>
            Başlat
          </button>
        </div>
        <div className="colon">:</div>

        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center"
          }}
        >
          <div className="minute">{String(seconds).padStart(2, '0')}</div>
          <button className="button" style={{ backgroundColor: '#ffb3b3' }} onClick={stopTimer}>
            Durdur
          </button>
        </div>
      </div>
      <p>{workMode ? "Çalışma Modu" : "Mola Modu"}</p>
    </div>
  );
}

export default Pomodoro;
