import './App.css';
import { Outlet } from "react-router-dom";
import 'animate.css';

import React, { useState, useEffect } from 'react';

function App() {
  const [selectedLanguage, setSelectedLanguage] = useState(localStorage.getItem('preferredLanguage') || 'en');
  const [translations, setTranslations] = useState({}); // Çeviri verilerini saklamak için bir state

  const changeLanguage = (language) => {
    setSelectedLanguage(language);
  };

  useEffect(() => {
    // Dil tercihini localStorage'dan al
    const storedLanguage = localStorage.getItem('preferredLanguage');
    if (storedLanguage) {
      setSelectedLanguage(storedLanguage);
    }
  }, []);

  useEffect(() => {
    // Dil tercihine uygun çeviri verilerini yükle
    import(`./${selectedLanguage}.json`)
      .then((module) => setTranslations(module.default))
      .catch((error) => console.error(error));
  }, [selectedLanguage]);

  return (
    <div>
      <div>
        <button onClick={() => changeLanguage('en')}>English</button>
        <button onClick={() => changeLanguage('tr')}>Türkçe</button>
      </div>

      <p>{translations.welcome_message}</p>
      <Outlet />
    </div>
  );
}

export default App;
