import './App.css';
import { Outlet } from "react-router-dom";
import 'animate.css';
import { useLanguage } from './Lang/LanguageProvider'; // LanguageContext'ten useLanguage'ı içe aktarın

import React from 'react';

function App() {
  const { loading, translations, changeLanguage } = useLanguage(); // useLanguage özelliğini kullanarak translations ve loading'e erişin

  return (
    <div>
      <div>
        <button style={{color:'#fff'}} onClick={() => changeLanguage('en-EN')}>English</button>
        <button style={{color:'#fff'}} onClick={() => changeLanguage('tr-TR')}>Türkçe</button>
      </div>

      {loading ? (
        <p style={{color:'#fff'}}>Loading...</p>
      ) : (
        <p style={{color:'#fff'}}>{translations.welcome_message}</p>
      )}

      <Outlet />
    </div>
  );
}

export default App;
