import './App.css';
import { Outlet } from "react-router-dom";
import 'animate.css';

import React, { useState } from 'react';
import Cookies from 'js-cookie';

const translations = {
  en: {
    welcome_message: "Hello, welcome to the React multi-language example!"
  },
  tr: {
    welcome_message: "Merhaba, React çoklu dil örneğine hoş geldiniz!"
  }
};

const preferredLanguage = Cookies.get('preferredLanguage');

function App() {
  const [selectedLanguage, setSelectedLanguage] = useState('en');

  const changeLanguage = (language) => {
    setSelectedLanguage(language);
    Cookies.set('preferredLanguage', language);
  };

  return (
    <div>
      <div>
        <button style={{color:'#fff'}} onClick={() => changeLanguage('en')}>English</button>
        <button style={{color:'#fff'}} onClick={() => changeLanguage('tr')}>Türkçe</button>
      </div>

      <p style={{color:'#fff'}}>{translations[selectedLanguage].welcome_message}</p>
      <Outlet />
    </div>
  );
}

export default App;
